mapper.select:
select
a.id, a.name, a.category_id, a.price, a.image, a.description, a.status, a.update_time,
b.name as category_name
from(
(select * from dish

    and name like concat('%',#{name},'%')
    and category_id = #{categoryId}
    and status = #{status}

) as a
left join category as b
on a.category_id=b.id
)

mapper.update:
update dish
name=#{name}, category_id=#{categoryId}, price=#{price}, image=#{image}, description=#{description}, status=#{status}, update_time=#{updateTime}, update_user=#{updateUser},
where id = #{id}
